<script setup>
import { ref, reactive } from "vue";
import { Head, useForm } from "@inertiajs/vue3";
import axios from "axios";

const isPasswordReset = ref(true);
const isAnimating = ref(false);
const showModal = ref(false);
const modalType = ref("success");
const modalTitle = ref("");
const modalMessage = ref("");

const passwordResetForm = useForm({
    email: "",
});

const demoForm = reactive({
    name: "",
    email: "",
    company: "",
    phone: "",
});

const toggleForm = () => {
    if (!isAnimating.value) {
        isAnimating.value = true;
        isPasswordReset.value = !isPasswordReset.value;
        setTimeout(() => {
            isAnimating.value = false;
        }, 1000);
    }
};

const submitPasswordReset = () => {
    passwordResetForm.post(route("password.email"), {
        preserveState: true,
        preserveScroll: true,
        onSuccess: (response) => {
            handleResponse(response);
        },
        onError: (errors) => {
            modalType.value = "error";
            modalTitle.value = "Erreur de réinitialisation";
            modalMessage.value = "Une erreur est survenue. Veuillez réessayer.";
            showModal.value = true;
        },
    });
};

const submitDemo = async () => {
    try {
        const response = await axios.post("/demo-request", demoForm);
        if (response.data.success) {
            modalType.value = "success";
            modalTitle.value = "Demande envoyée";
            modalMessage.value =
                "Votre demande de démo a été envoyée avec succès!";
            showModal.value = true;
            // Réinitialiser le formulaire
            Object.keys(demoForm).forEach((key) => (demoForm[key] = ""));
        } else {
            throw new Error("La demande n'a pas pu être traitée.");
        }
    } catch (error) {
        console.error("Erreur lors de l'envoi de la demande de démo:", error);
        modalType.value = "error";
        modalTitle.value = "Erreur";
        modalMessage.value =
            "Une erreur est survenue lors de l'envoi de votre demande. Veuillez réessayer.";
        showModal.value = true;
    }
};

const handleResponse = (response) => {
    if (response.props.status) {
        modalType.value = "success";
        modalTitle.value = isPasswordReset.value
            ? "Email envoyé"
            : "Demande envoyée";
        modalMessage.value = response.props.status;
        showModal.value = true;
        if (!isPasswordReset.value) {
            Object.keys(demoForm).forEach((key) => (demoForm[key] = ""));
        }
    } else if (response.props.errors) {
        modalType.value = "error";
        modalTitle.value = "Erreur";
        modalMessage.value =
            Object.values(response.props.errors)[0] ||
            "Une erreur est survenue. Veuillez réessayer.";
        showModal.value = true;
    } else {
        modalType.value = "error";
        modalTitle.value = "Erreur";
        modalMessage.value =
            "Une erreur inattendue est survenue. Veuillez réessayer.";
        showModal.value = true;
    }
};

const closeModal = () => {
    showModal.value = false;
};
</script>

